import{_ as o,c as t,o as a,a2 as e}from"./chunks/framework.BqxRur5o.js";const p=JSON.parse('{"title":"历史记录","description":"","frontmatter":{},"headers":[],"relativePath":"guide/history.md","filePath":"guide/history.md","lastUpdated":1725611655000}'),r={name:"guide/history.md"},i=e('<h1 id="历史记录" tabindex="-1">历史记录 <a class="header-anchor" href="#历史记录" aria-label="Permalink to &quot;历史记录&quot;">​</a></h1><p><strong>历史记录工具</strong>是用来回溯或撤销之前编辑步骤的工具，可以帮助用户轻松恢复图像的不同状态。</p><h2 id="_1-历史记录面板" tabindex="-1">1. 历史记录面板 <a class="header-anchor" href="#_1-历史记录面板" aria-label="Permalink to &quot;1. 历史记录面板&quot;">​</a></h2><ul><li><strong>打开历史记录面板</strong>：点击 <code>窗口 &gt; 历史记录</code>，历史记录面板会显示从打开文件开始的每一步操作。</li><li><strong>回溯到某一步</strong>：点击面板中的某一步，Photoshop 会立即将图像恢复到该步骤状态。</li><li><strong>步数限制</strong>：默认最多记录 50 步操作，用户可以通过 <code>Photoshop &gt; 首选项 &gt; 性能</code> 来增加或减少历史记录的步数。</li></ul><h2 id="_2-历史记录画笔工具" tabindex="-1">2. 历史记录画笔工具 <a class="header-anchor" href="#_2-历史记录画笔工具" aria-label="Permalink to &quot;2. 历史记录画笔工具&quot;">​</a></h2><ul><li><strong>快捷键</strong>：<code>Y</code></li><li><strong>功能</strong>：使用历史记录画笔，可以将图像的某些区域恢复到某个特定的历史状态，而不是整个图像。</li><li><strong>操作</strong>：在历史记录面板中选择要恢复的状态，然后使用历史记录画笔在图像上涂抹，恢复特定部分的历史状态。</li></ul><h2 id="_3-撤销-重做" tabindex="-1">3. 撤销/重做 <a class="header-anchor" href="#_3-撤销-重做" aria-label="Permalink to &quot;3. 撤销/重做&quot;">​</a></h2><ul><li><strong>撤销</strong>：按 <code>Cmd + Z</code> 撤销上一步操作。Photoshop 支持多步撤销，按 <code>Cmd + Option + Z</code> 可以连续撤销多个步骤。</li><li><strong>重做</strong>：按 <code>Cmd + Shift + Z</code> 恢复已经撤销的操作。</li></ul><h2 id="_4-快照" tabindex="-1">4. 快照 <a class="header-anchor" href="#_4-快照" aria-label="Permalink to &quot;4. 快照&quot;">​</a></h2><ul><li><strong>创建快照</strong>：点击历史记录面板下方的“创建快照”按钮，可以将图像当前的状态保存为快照。</li><li><strong>恢复快照</strong>：点击快照可以恢复图像到创建快照时的状态，而不影响完整的历史记录。</li></ul><h2 id="_5-清除历史" tabindex="-1">5. 清除历史 <a class="header-anchor" href="#_5-清除历史" aria-label="Permalink to &quot;5. 清除历史&quot;">​</a></h2><p>右键点击历史记录面板中的某一步，选择“清除”，可以删除特定的历史步骤，或者通过 <code>清除所有</code> 清空整个历史记录。</p>',12),s=[i];function l(n,d,h,c,_,g){return a(),t("div",null,s)}const m=o(r,[["render",l]]);export{p as __pageData,m as default};
